mapper.update:
UPDATE "CART" SET
CART_COUNT = CART_COUNT - 1
WHERE ITEM_NO = #{itemNo}
AND SALE_TYPE = #{type}
AND MEMBER_NO = #{memberNo}

UPDATE "CART" SET
CART_COUNT = CART_COUNT + 1
WHERE ITEM_NO = #{itemNo}
AND SALE_TYPE = #{type}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM "CART"
WHERE ITEM_NO = #{itemNo}
AND SALE_TYPE = #{type}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) FROM "CART"
WHERE MEMBER_NO = #{memberNo}
AND ITEM_NO = #{itemNo}
AND SALE_TYPE = #{type}

